import random

class CreditCard:
    def __init__(self, card_number, expiration_date=None, cvv=None):
        self.card_number = card_number
        self.expiration_date = expiration_date or f"{random.randint(1, 12)}/{random.randint(22, 27)}"
        self.cvv = cvv if cvv != "" else f"{random.randint(100, 999)}"

class CreditCardGenerator:
    def __init__(self):
        self.cards = []
    
    def generate_card(self, bin_num, num_cards, name_list, expiration_date=None, cvv=None):
        for i in range(num_cards):
            # Generate random digits for the remaining digits in credit card number
            remaining_digits = ''.join(random.choice('0123456789') for _ in range(10))

            # Combine BIN, remaining digits, and a check digit to form complete credit card number
            cc_num = bin_num + remaining_digits
            check_digit = str(random.randint(0, 9))
            cc_num += check_digit

            # Format credit card number into groups of four digits separated by spaces
            formatted_cc_num = ' '.join([cc_num[i:i+4] for i in range(0, len(cc_num), 4)])

            # Randomly select a cardholder name
            name = random.choice(name_list)

            # Generate expiry date and CVV based on user input or randomly
            if expiration_date is not None and cvv != "":
                card = CreditCard(cc_num, expiration_date=expiration_date, cvv=cvv)
            else:
                card = CreditCard(cc_num, expiration_date=None, cvv=None)
            
            # Add card to list of generated cards
            self.cards.append(card)

            # Print generated credit card details
            print("\nCredit Card ", i+1)
            print("Cardholder Name: ", name)
            print("Credit Card Number: ", formatted_cc_num[:-1])
            print("Expiry Date: ", card.expiration_date)
            print("CVV: ", card.cvv)

